import { useState, useEffect } from 'react'
import axios from 'axios'
import Countries from './components/Countries'
import SearchFilter from './components/SearchFilter'

const App = () => {
  const [countries, setCountries] = useState([])
  const [searchTerm, setSearchTerm] = useState("")

  useEffect(() => {
      console.log('effect')
      axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log('promise fulfiled')
        setCountries(response.data)
      })
  },[])



  console.log('render', countries.map(obj => obj.name.official))

//  console.log('counties', countries.length)
//  console.log('country1', countries[0])
//  console.log('country1', countries[0].name.official)

  // const handleInputFilter = (event) =>{
  //   event.preventDefault()
  //   console.log(searchTerm)
  //   setSearchTerm(event.target.value)
  // }
  // console.log(countries)

  const handleSearchInputFilter = (event) => {
    setSearchTerm(event.target.value)
  }

  return (

    <div>
      <input 
        value={searchTerm}  
        onChange={(event) => handleSearchInputFilter(event)}
      />

      {countries.map(obj => <div>
                              <p>{obj.name.official}</p>
                              <p>{obj.capital}</p>
                              <p>{obj.region}</p>
                              <p>{obj.flag}</p>
                              <hr/>
                            </div>
                            )

    /* Search country <SearchFilter value={searchTerm}
          handleChange={(event)=> handleInputFilter(event)} 
    />   */}

    {/* <Countries countries={countries}/>     */}
    </div>
  );
}

export default App;
