import { useState, useEffect } from 'react'
import axios from 'axios'
import Countries from './components/Countries'
import SearchFilter from './components/SearchFilter'

const App = () => {
  const [countries, setCountries] = useState([])
  const [searchTerm, setSearchTerm] = useState("")


  useEffect(() => {
      axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  },[])

  const fetch = (e) => {
    e.preventDefault()
    setSearchTerm(e.target.value)
    console.log(searchTerm)
    }

  const countriesToDisplay = countries.filter(country => country.name.common.toLowerCase().indexOf(searchTerm) > -1)

  return (
    <>
    <form onChange={fetch}>
        find country<input/>
      </form>
    {
      searchTerm
      ? countriesToDisplay.length < 10
        ? countriesToDisplay.length === 1
          ? countriesToDisplay.map(country => <div>
            <section>
              <h2>Country: {country.name.common}</h2>
              <p>Capital: {country.capital}</p>
              <p>Area: {country.area}</p>
              <h2>languages:</h2>
              <ul>
                {country.languages 
                  ? Object.values(country.languages).map(i => <li>{i}</li>)
                  : null}
              </ul>
              <div alt='flag'>{country.flag}</div>
            </section>
            </div>)

          :countriesToDisplay.map(country => <div>{country.name.common}</div>)    

          : "Too many matches, specify another filter"

      : ""  
    }
    </>
  );
}

export default App;
