import { useState, useEffect } from 'react'
import personsService from './services/persons'
import Persons from './components/Persons'
import SearchFilter from './components/SearchFilter'
import AddPersonForm from './components/AddPersonForm'

const App = () => {
  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState()
  const [newNumber, setNewNumber] = useState()
  const [searchTerm, setSearchTerm] = useState()


  useEffect(() => {
    console.log('effect')
    personsService    
        .getAll()      
        .then( initialPerson => {
            console.log('promise fulfilled') 
            setPersons(initialPerson)

        })
      },[]) 

  const addPerson = (event) => {
    event.preventDefault()
    const personObj = {
      ...(isFound === true && alert( `The name ${newName} already exits to the phone book`)),
      ...(isFound === false &&  {name:newName}),
      ...({number:newNumber}),
    } 

    console.log('promise fulfilled') 

    personsService 
            .create(personObj)
            .then(returnedPerson => {
                console.log('promise fulfilled', returnedPerson) 
                setPersons(persons.concat(returnedPerson))
                setNewName('')
                setNewNumber('')

            })
    }

    const isFound = persons.some(element => {
      // Use's the some array function to check's if the name exists in the persons array array.
      // It also checks if the name's spelled with capitals,
      // it only looks if the first name is capitalized 
      if (element.name === newName) {
        return true
      }
      return false
    })

    const handleNameFilter = (event) => {   
      console.log(event.target.value)    
      setSearchTerm(event.target.value)  
  }

  const handleNameChange = (event) => {
    console.log(event.target.value)    
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)   
    setNewNumber(event.target.value)
  }

  const PersonsToShow = !searchTerm
  ? persons
  : persons.filter(person => 
          person.name.toLowerCase().includes(searchTerm.toLowerCase()))

  return (
  <>  
      <h2>Phone book</h2>
      <SearchFilter 
          searchTerm={searchTerm} 
          handleChange={(event)=> handleNameFilter(event)}
          abel={searchTerm}
        /> 

      <Persons persons={PersonsToShow}/>

       <AddPersonForm
           onSubmit={addPerson}
           name={newName}
           number={newNumber}
           nameChangeHandler={handleNameChange}
           numberChangeHandler={handleNumberChange}

       />
        {/* <Form submitEntry={addPerson}
              name={newName} 
              handleName={(event) => handleNameChange(event)}
              number={newNumber}
              handleNumber={(event) => handleNumberChange(event)}
        />        */}
  </>
  )
  }

  export default App
