const Persons = (props) => {
    const liStyle = {
        color: 'green',
        fontStyle: 'italic'
      }

    return(

 <ul>
{props.persons.map((person) => <li key={person.id}>
                                {person.name} : {person.number}
                            <button onClick={() => props.onDelete(person.id)} >Delete</button>      
                            </li>)}
 </ul>
 )}

export default Persons
