import { useState } from 'react'
import personsService from './services/persons'

function AddPersonForm(props) {
  const [state, setState] = useState({
    name : "",
    number: ""
  })

  function handleChange(evt) {
    const value = evt.target.value; 
    setState({
      ...state,
      [evt.target.name]: value
    });

  }

  const addPerson = (event) => {
    event.preventDefault()
    const personObj = {
      ...(isFound === true && alert( `The name ${state.name} already exits to the phone book`)),
      ...(isFound === false &&  {name:state.name}),
      ...({number:state.number}),
    } 

    personsService.create(personObj)
    }

    const isFound = props.persons.some(element => {
      // Use's the some array function to check's if the name exists in the persons array array.
      // It also checks if the name's spelled with capitals,
      // it only looks if the first name is capitalized 
      if (element.name === state.name) {
        return true
      }
      return false
    })

  return (
    <form>
      <label>
        Name:
        <input
          type="text"
          name="name"
          value={state.name}
          onChange={handleChange}
        />
        {console.log(state.name)}
      </label>
      <label>
        Number:
        <input
          type="int"
          name="number"
          value={state.number}
          onChange={handleChange}
        />
         {console.log(state.number)}
      </label>
      <button onSubmit={addPerson}></button>
    </form>
  );
  }

export default AddPersonForm
